import java.io.*;
public class Even {

	public static void main(String[] args)
	{
		// TODO Auto-generated method stub
       int i=11;
       System.out.println("Even numbers between 10 and 100");
       while(i<100)
       {
    	   if((i%2)==0)
    	   {
    		   System.out.println(i);
           }
    	   i++;
	   }

   }
}
